import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGithub, faLinkedin } from '@fortawesome/free-brands-svg-icons';
import { faAward, faEnvelope, faUserGraduate, faAnglesDown } from '@fortawesome/free-solid-svg-icons';
import '../App.css';

// Import images
import profilePic from '../assets/pictures/profile-pic.jpg';
import project1Pic from '../assets/pictures/project-1.JPG';
import project2Pic from '../assets/pictures/project-2.jpeg';

const MainContent = () => {
    const openPDF = () => {
        window.open('/pictures/Resume-pic.pdf', '_blank');
    };

    const scrollToAbout = () => {
        const AboutSection = document.getElementById('About');
        if (AboutSection) {
            AboutSection.scrollIntoView({ behavior: 'smooth' });
        }
    };

    const scrollToExperience = () => {
        const experienceSection = document.getElementById('Experience');
        if (experienceSection) {
            experienceSection.scrollIntoView({ behavior: 'smooth' });
        }
    };

    const scrollToProjects = () => {
        const ProjectsSection = document.getElementById('Projects');
        if (ProjectsSection) {
